:root {
  /* Brand colors */
  --chat--color-primary: #417fb8;
  --chat--color-primary-shade-50: #78a2c6;
  --chat--color-primary-shade-100: #3a73a7;
  --chat--color-secondary: #bad1e4;
  --chat--color-white: #ffffff;
  --chat--color-light: #f7fafb;
  --chat--color-light-shade-50: #e9f1f6;
  --chat--color-light-shade-100: #d8e2eb;
  --chat--color-dark: #1f2933;

  /* Header */
  --chat--header--background: var(--chat--color-light);
  --chat--header--padding: 1.2rem;
  --chat--heading--font-size: 1.8rem;
  --chat--subtitle--font-size: 1.05rem;

  /* Messages */
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: #ffffff;
  --chat--message--bot--background: var(--chat--color-light-shade-50);
  --chat--message--bot--border: 1px solid var(--chat--color-light-shade-100);

  /* Input & Send button */
  --chat--input--border: 1px solid var(--chat--color-light-shade-100);
  --chat--input--border-radius: 12px;
  --chat--input--send--button--background: #417fb8;
  --chat--input--send--button--color: #ffffff;
  --chat--input--send--button--background-hover: #3a73a7;
  --chat--input--send--button--color-hover: #ffffff;

  /* Toggle */
  --chat--toggle--background: linear-gradient(135deg, #417fb8, #78a2c6);
  --chat--toggle--color: #ffffff;
}

/* CENTER the entire header */
.chat-header {
  text-align: center !important;
  display: flex !important;
  flex-direction: column !important;
  align-items: center !important;
  justify-content: center !important;
}

/* Title - blue, bold, centered */
.chat-header h1,
.chat-header .title,
.chat-title {
  text-align: center !important;
  color: #417fb8 !important;
  font-weight: 800 !important;
  font-size: 1.8rem !important;
  margin: 0 auto 0.4rem !important;
  width: 100% !important;
}

/* Subtitle - BLACK, centered */
.chat-header .subtitle,
.chat-header p {
  text-align: center !important;
  color: #1f2933 !important;
  font-size: 1.05rem !important;
  font-weight: 500 !important;
  margin: 0 auto !important;
  width: 100% !important;
}

/* Send button - blue circle with white arrow */
button[aria-label*="Send"],
.chat-input button[type="submit"],
.send-button {
  background: #417fb8 !important;
  color: #ffffff !important;
  border-radius: 50% !important;
  width: 40px !important;
  height: 40px !important;
  min-width: 40px !important;
  padding: 0 !important;
  display: flex !important;
  align-items: center !important;
  justify-content: center !important;
  box-shadow: 0 4px 10px rgba(65,127,184,0.3);
}

button[aria-label*="Send"]:hover,
.send-button:hover {
  background: #3a73a7 !important;
  transform: translateY(-2px);
}

