/**
 * Validate & normalize Products, Customers, Orders from Merge node.
 * Friendly messages + robust detection for "error arrays" from Sheets nodes.
 *
 * Required headers:
 * Products:  "Product ID", "Product Name", "Category", "Unit Price ($)", "Stock Quantity"
 * Customers: "Customer ID", "First Name", "Last Name", "Email", "City"
 * Orders:    "Order ID", "Customer ID", "Product ID", "Order Date", "Quantity", "Total Amount ($)"
 */

const input = items[0]?.json || {};

// ---------- helpers ----------
const num = (v) => {
  if (v === null || v === undefined) return null;
  const n = parseFloat(String(v).replace(/[^\d.-]/g, ''));
  return Number.isFinite(n) ? n : null;
};

const toISOFromDDMMYYYY = (s) => {
  if (!s) return null;
  if (s instanceof Date && !isNaN(s)) return s.toISOString().slice(0, 10);
  const m = String(s).match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/);
  if (!m) {
    const d = new Date(s);
    return isNaN(d) ? null : d.toISOString().slice(0, 10);
  }
  const dd = String(m[1]).padStart(2, '0');
  const mm = String(m[2]).padStart(2, '0');
  const yyyy = m[3];
  return `${yyyy}-${mm}-${dd}`;
};

// Treat arrays that only contain error rows as "missing tab"
const isErrorArray = (arr) => {
  if (!Array.isArray(arr) || arr.length === 0) return false;
  // common shapes: [{ error: "..." }], [{ message: "...", code: ... }], or a single string "error"
  const first = arr[0];
  if (typeof first === 'string') return true;
  if (first && typeof first === 'object') {
    const keys = Object.keys(first);
    if (keys.length === 1 && (keys[0] === 'error' || keys[0] === 'message')) return true;
    // sometimes error text is nested
    if (('error' in first) || ('message' in first && String(first.message).toLowerCase().includes('not found'))) {
      return true;
    }
  }
  return false;
};

// ---------- requirements ----------
const REQUIRED = {
  products: [
    "Product ID",
    "Product Name",
    "Category",
    "Unit Price ($)",
    "Stock Quantity",
  ],
  customers: [
    "Customer ID",
    "First Name",
    "Last Name",
    "Email",
    "City",
  ],
  orders: [
    "Order ID",
    "Customer ID",
    "Product ID",
    "Order Date",
    "Quantity",
    "Total Amount ($)",
  ],
};

const TAB_LABELS = { products: "Products", customers: "Customers", orders: "Orders" };

// Pull current values
const productsIn  = input.products;
const customersIn = input.customers;
const ordersIn    = input.orders;

// ---------- 1) Tabs present? (including "error arrays") ----------
const missingTabs = [];
for (const key of Object.keys(REQUIRED)) {
  const val = input[key];
  const absent = !Array.isArray(val);
  const errorLike = isErrorArray(val);
  if (absent || errorLike) {
    missingTabs.push(key);
  }
}

if (missingTabs.length) {
  const names = missingTabs.map(k => `â€œ${TAB_LABELS[k]}â€`).join(", ");
  const plural = missingTabs.length > 1 ? "tabs" : "tab";
  const message =
    `I couldnâ€™t find the ${plural} ${names} in your Google Sheet.\n\n` +
    `Please check the tab name${missingTabs.length>1?'s':''} at the bottom of your sheet.\n` +
    `They should be named exactly: â€œProductsâ€, â€œCustomersâ€, and â€œOrdersâ€.\n\n` +
    `If you need help, contact the admin.`;

  return [{
    json: {
      error: true,
      message,
      health: {
        sheets_ok: false,
        missing_tabs: missingTabs.map(k => TAB_LABELS[k]),
        counts: {
          products: Array.isArray(productsIn)  && !isErrorArray(productsIn)  ? productsIn.length  : 0,
          customers: Array.isArray(customersIn) && !isErrorArray(customersIn) ? customersIn.length : 0,
          orders: Array.isArray(ordersIn)    && !isErrorArray(ordersIn)    ? ordersIn.length    : 0,
        }
      }
    }
  }];
}

// ---------- 2) Empty tabs? (friendly message) ----------
const countsNow = {
  products: (Array.isArray(productsIn)  ? productsIn.length  : 0),
  customers:(Array.isArray(customersIn) ? customersIn.length : 0),
  orders:   (Array.isArray(ordersIn)    ? ordersIn.length    : 0),
};

const emptyTabs = [];
if (countsNow.products === 0) emptyTabs.push("Products");
if (countsNow.customers === 0) emptyTabs.push("Customers");
if (countsNow.orders === 0) emptyTabs.push("Orders");

if (emptyTabs.length) {
  const plural = emptyTabs.length > 1 ? "tabs are" : "tab is";
  const message =
    `Your sheet is connected, but the ${plural} empty: ${emptyTabs.join(", ")}.\n\n` +
    `Please add at least one data row under the header (row 1) in each listed tab, then try again.\n` +
    `If you need help, contact the admin.`;

  return [{
    json: {
      error: true,
      message,
      health: { sheets_ok: true, missing_tabs: [], counts: countsNow }
    }
  }];
}

// ---------- 3) Headers correct? (check only if there are rows & not error-like) ----------
const headerIssues = [];
for (const [key, requiredCols] of Object.entries(REQUIRED)) {
  const rows = input[key] || [];
  if (!rows.length || isErrorArray(rows)) continue; // skip; already handled above
  const found = Object.keys(rows[0] || {});
  const missing = requiredCols.filter(c => !found.includes(c));
  if (missing.length) {
    headerIssues.push({ tabName: TAB_LABELS[key], missing, found });
  }
}

if (headerIssues.length) {
  const lines = headerIssues.map(h =>
    `â€¢ In the â€œ${h.tabName}â€ tab, I couldnâ€™t find: ${h.missing.join(", ")}.\n` +
    `  Please rename your header row to match exactly.\n` +
    `  Currently I see: ${h.found.join(", ")}`
  ).join("\n\n");

  const message =
    `Some column headers donâ€™t match the expected template.\n\n${lines}\n\n` +
    `Tip: Header names must match exactly (including spaces and symbols like â€œ($)â€).\n` +
    `If you need help, contact the admin.`;

  return [{
    json: {
      error: true,
      message,
      health: { sheets_ok: true, missing_tabs: [], counts: countsNow }
    }
  }];
}

// ---------- 4) Normalize Products ----------
const productsRaw = input.products || [];
const products = productsRaw.map(r => ({
  product_id: r["Product ID"] ?? null,
  product_name: r["Product Name"] ?? null,
  category: r["Category"] ?? null,
  unit_price: num(r["Unit Price ($)"]),
  stock_quantity: num(r["Stock Quantity"]),
}));

// Price lookup for fallback totals
const priceByProductId = new Map(
  products.filter(p => p.product_id && p.unit_price !== null)
          .map(p => [String(p.product_id), p.unit_price])
);

// ---------- 5) Normalize Customers ----------
const customersRaw = input.customers || [];
const customers = customersRaw.map(r => ({
  customer_id: r["Customer ID"] ?? null,
  first_name: r["First Name"] ?? null,
  last_name: r["Last Name"] ?? null,
  email: r["Email"] ?? null,
  city: r["City"] ?? null,
}));

// ---------- 6) Normalize Orders ----------
const ordersRaw = input.orders || [];
const orders = ordersRaw.map(r => {
  const quantity = num(r["Quantity"]);
  let total_amount = num(r["Total Amount ($)"]);

  if (total_amount === null) {
    const pid = r["Product ID"] ? String(r["Product ID"]) : null;
    const unitPrice = pid ? priceByProductId.get(pid) : null;
    if (quantity !== null && unitPrice !== null) {
      total_amount = Number((quantity * unitPrice).toFixed(2));
    }
  }

  return {
    order_id: r["Order ID"] ?? null,
    customer_id: r["Customer ID"] ?? null,
    product_id: r["Product ID"] ?? null,
    order_date_raw: r["Order Date"] ?? null,
    order_date_iso: toISOFromDDMMYYYY(r["Order Date"]),
    quantity,
    total_amount,
  };
});

// ---------- 7) Health + return ----------
const health = {
  sheets_ok: true,
  missing_tabs: [],
  counts: {
    products: products.length,
    customers: customers.length,
    orders: orders.length,
  }
};

return [{ json: { error: false, message: null, health, products, customers, orders } }];
